scripts.solr-create-collection:
bash -c 'set -e; COLLECTION=pdc-core-dev; CONFIGSET=pdc_discovery; # Upload configset if missing
if ! curl -fsS "http://solr:SolrRocks@$SOLR_HOST:$SOLR_PORT/solr/admin/configs?action=LIST&wt=json" | grep -q "\"$CONFIGSET\""; then
  solr zk upconfig -z localhost:9983 -n "$CONFIGSET" -d "$PWD/solr/conf";
fi
# Create collection if missing
if curl -fsS "http://solr:SolrRocks@$SOLR_HOST:$SOLR_PORT/solr/admin/collections?action=LIST&wt=json" | grep -q "\"$COLLECTION\""; then
  echo "Collection $COLLECTION already exists";
else
  solr create_collection -c "$COLLECTION" -n "$CONFIGSET" -shards 1 -replicationFactor 1 -p "$SOLR_PORT";
fi'
